import React from 'react';
import { useParams } from 'react-router-dom';

const teams = [
  {
    id: 'dragoes-do-norte',
    name: 'Dragões do Norte',
    image: '/images/dragons.png',
    history: 'Fundado em 1920, o Dragões do Norte nasceu em uma pequena vila costeira. O time começou como um grupo de pescadores que jogavam futebol nas horas vagas. Hoje, é um dos maiores clubes do país.',
    titles: [
      'Campeonato Nacional: 12 vezes',
      'Copa Nacional: 9 vezes',
    ],
    achievements: 'Conquistaram o título continental em 2000 com uma virada histórica nos últimos minutos.',
    stadium: 'Estádio dos Dragões, com capacidade para 60.000 torcedores.',
  },
  {
    id: 'leoes-da-serra',
    name: 'Leões da Serra',
    image: '/images/lions.png',
    history: 'Criado em 1935 por mineiros, o Leões da Serra representa a força e a resistência de sua região.',
    titles: [
      'Campeonato Nacional: 8 vezes',
      'Copa Nacional: 6 vezes',
    ],
    achievements: 'Conhecidos por sua defesa impenetrável, venceram o campeonato nacional de 1985 sem sofrer gols.',
    stadium: 'Arena da Serra, com capacidade para 45.000 torcedores.',
  },
  {
    id: 'aguias-do-sul',
    name: 'Águias do Sul',
    image: '/images/eagles.png',
    history: 'Fundado em 1940, o Águias do Sul começou como um time escolar e rapidamente ganhou notoriedade.',
    titles: [
      'Campeonato Nacional: 10 vezes',
      'Copa Nacional: 7 vezes',
    ],
    achievements: 'Em 2010, venceram o campeonato continental com uma campanha invicta.',
    stadium: 'Estádio das Águias, com capacidade para 55.000 torcedores.',
  },
  {
    id: 'guerreiros-do-oeste',
    name: 'Guerreiros do Oeste',
    image: '/images/warriors.png',
    history: 'Criado em 1950 por agricultores, o Guerreiros do Oeste simboliza a luta e a perseverança.',
    titles: [
      'Campeonato Nacional: 5 vezes',
      'Copa Nacional: 4 vezes',
    ],
    achievements: 'Em 1995, eliminaram três campeões consecutivos para conquistar a Copa Nacional.',
    stadium: 'Arena dos Guerreiros, com capacidade para 40.000 torcedores.',
  },
  {
    id: 'estrelas-do-leste',
    name: 'Estrelas do Leste',
    image: '/images/stars.png',
    history: 'Fundado em 1960, o Estrelas do Leste é conhecido por revelar grandes talentos.',
    titles: [
      'Campeonato Nacional: 6 vezes',
      'Copa Nacional: 5 vezes',
    ],
    achievements: 'Em 2005, venceram o campeonato nacional com o maior número de gols marcados na história.',
    stadium: 'Estádio das Estrelas, com capacidade para 50.000 torcedores.',
  },
  {
    id: 'falcoes-de-prata',
    name: 'Falcões de Prata',
    image: '/images/falcons.png',
    history: 'Criado em 1970, o Falcões de Prata é famoso por sua velocidade e estilo ofensivo.',
    titles: [
      'Campeonato Nacional: 7 vezes',
      'Copa Nacional: 6 vezes',
    ],
    achievements: 'Em 2015, marcaram 10 gols em uma única partida da final.',
    stadium: 'Arena dos Falcões, com capacidade para 48.000 torcedores.',
  },
  {
    id: 'lobos-do-cerrado',
    name: 'Lobos do Cerrado',
    image: '/images/wolves.png',
    history: 'Fundado em 1980, o Lobos do Cerrado representa a força da natureza e a união de sua comunidade.',
    titles: [
      'Campeonato Nacional: 4 vezes',
      'Copa Nacional: 3 vezes',
    ],
    achievements: 'Em 2018, venceram o campeonato nacional com um time formado apenas por jogadores locais.',
    stadium: 'Estádio dos Lobos, com capacidade para 35.000 torcedores.',
  },
  {
    id: 'trovoes-do-vale',
    name: 'Trovões do Vale',
    image: '/images/thunders.png',
    history: 'Criado em 1990, o Trovões do Vale é conhecido por sua torcida apaixonada e vibrante.',
    titles: [
      'Campeonato Nacional: 3 vezes',
      'Copa Nacional: 2 vezes',
    ],
    achievements: 'Em 2020, venceram o campeonato nacional com uma virada histórica na final.',
    stadium: 'Arena dos Trovões, com capacidade para 42.000 torcedores.',
  },
  {
    id: 'panteras-negras',
    name: 'Panteras Negras',
    image: '/images/panthers.png',
    history: 'Fundado em 1925, o Panteras Negras é um dos clubes mais antigos e respeitados do país.',
    titles: [
      'Campeonato Nacional: 15 vezes',
      'Copa Nacional: 10 vezes',
    ],
    achievements: 'Em 1980, venceram o campeonato continental com uma campanha impecável.',
    stadium: 'Estádio das Panteras, com capacidade para 65.000 torcedores.',
  },
];

const TeamDetail = () => {
  const { id } = useParams();
  const team = teams.find((team) => team.id === id);

  if (!team) {
    return <p>Time não encontrado.</p>;
  }

  return (
    <div className="team-detail">
      <img src={team.image} alt={team.name} className="team-detail-image" />
      <h2>{team.name}</h2>
      <p>{team.history}</p>
      <h3>Títulos Importantes</h3>
      <ul>
        {team.titles.map((title, index) => (
          <li key={index}>{title}</li>
        ))}
      </ul>
      <h3>Feitos Memoráveis</h3>
      <p>{team.achievements}</p>
      <h3>Estádio</h3>
      <p>{team.stadium}</p>
    </div>
  );
};

export default TeamDetail;
